import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'

function Parent({ node, deleteNode, rename, addNode }) {
  const [state, setState] = useState()

  const renameChild = (id) => {
    setState({
      ...state,
      children: [
        ...state.children.map((node) => {
          if (node.id === id) {
            node.name = 'kek'

            return node
          } else {
            return node
          }
        }),
      ],
    })
  }

  const addNodeChild = () => {
    setState({
      ...state,
      children: [...state.children, { id: v4(), name: 'empty', children: [] }],
    })
  }

  const deleteNodeChild = (id) => {
    setState({
      ...state,
      children: [...state.children.filter((node) => node.id !== id)],
    })
  }

  useEffect(() => {
    node ? setState(node) : setState({ id: v4(), name: 'empty', children: [] })
  }, [])

  return (
    <ul>
      {state && <li>{state.name}</li>}
      <button onClick={() => addNode()}>add</button>
      <button
        onClick={() => {
          console.log(state)
          deleteNode(state.id)
        }}
      >
        del
      </button>
      <button
        onClick={() => {
          console.log(state)
          rename(state.id)
        }}
      >
        rename
      </button>
      {state &&
        state.children.map((node) => (
          <Parent
            node={node}
            deleteNode={deleteNodeChild}
            rename={renameChild}
            addNode={addNodeChild}
          />
        ))}
      {/* {state.map((item, index) => {
        return (
          <li>
            <Child item={item} arr={state} setItem={setState} />
          </li>
        );
      })} */}
    </ul>
  )
}

export default Parent
