import React, { useState } from 'react'
import { v4 } from 'uuid'
import Parent from './Parent'

function App() {
  const [state, setState] = useState([{ id: v4(), name: 'root', children: [] }])

  return (
    <div className="App">
      {state &&
        state.map((node) => (
          <Parent
            node={node}
            addNode={() => setState([...state.map((node) => {})])}
          />
        ))}
      <button onClick={() => console.log(state)}>nodes</button>
    </div>
  )
}

export default App
