import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'

const Child = ({
  node,
  id,
  addNodeHandler,
  deleteNodeHandler,
  updateNodeState,
  statusUpdateRoot,
  updateChildrenState,
  setStatusUpdateChildren,
  statusUpdateChildren,
}) => {
  const [children, setChildren] = useState(node)
  const rootId = id

  const addChildrenNode = (id, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const deleteChildrenNode = (id, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [...node.children.filter((item) => item.id !== childId)],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const updateChildren = (id, child) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return { ...node, children: child }
        } else {
          return node
        }
      }),
    ])
  }

  useEffect(() => {
    updateChildrenState(rootId, children)
  }, [children])

  useEffect(() => {
    if (statusUpdateRoot) {
      setChildren(node)
    }
  }, [statusUpdateRoot])

  useEffect(() => {
    if (statusUpdateChildren) {
      setChildren(node)
      setStatusUpdateChildren()
    }
  }, [statusUpdateChildren])

  return (
    <ul>
      {children &&
        children.map((node) => (
          <li key={node.id}>
            {node.id}
            <button onClick={() => addNodeHandler(rootId, node.id)}>+</button>
            <button onClick={() => deleteNodeHandler(rootId, node.id)}>
              -
            </button>
            <button onClick={() => console.log(children)}>log</button>

            <Child
              node={node.children}
              id={node.id}
              addNodeHandler={addChildrenNode}
              statusUpdateRoot={statusUpdateRoot}
              updateNodeState={updateNodeState}
              updateChildrenState={updateChildren}
              deleteNodeHandler={deleteChildrenNode}
            />
          </li>
        ))}
      {/* {}
        <button onClick={() => addNode(children.id)}>+</button>
        <button onClick={() => deleteNode(children.id)}>+</button> */}
      {/* {children.children.map((node, index) => (
          <Child key={node.id} node={node} />
        ))} */}
    </ul>
  )
}

export default Child
