import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'
import Root from './Root'
let update
const Tree = () => {
  ;[, update] = useState()
  const [nodes, setNodes] = useState([
    // {
    //   id: v4(),
    //   name: 'root',
    //   children: [
    //     {
    //       id: v4(),
    //       name: 'child',
    //       children: [
    //         {
    //           id: v4(),
    //           name: 'child',
    //           children: [
    //             {
    //               id: v4(),
    //               name: 'child',
    //               children: [{ id: v4(), name: 'child', children: [] }],
    //             },
    //           ],
    //         },
    //       ],
    //     },
    //     { id: v4(), name: 'child', children: [] },
    //   ],
    // },
    // {
    //   id: v4(),
    //   name: 'child',
    //   children: [
    //     {
    //       id: v4(),
    //       name: 'child',
    //       children: [
    //         {
    //           id: v4(),
    //           name: 'child',
    //           children: [
    //             {
    //               id: v4(),
    //               name: 'child',
    //               children: [{ id: v4(), name: 'child', children: [] }],
    //             },
    //           ],
    //         },
    //       ],
    //     },
    //   ],
    // },
  ])

  const [updateNodes, setUpdateNodes] = useState(false)

  const [idRoot, setIdRoot] = useState('')
  const [updateChild, setUpdateChild] = useState(false)

  useEffect(() => {
    console.log('tree update')
    // console.log(updateNodes, idRoot)

    setUpdateNodes(true)

    // setUpdateNodes(true)

    // setNodes((prev) => nodes)
  }, [nodes])

  const addChildrenNode = (id, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])

    setUpdateChild(true)
  }

  const setNewStateNodes = (id, newChildrens) => {
    // const kek = newChildrens.id
    // console.log(newChildrens)
    // console.log
    // console.log('updates?')
    setNodes([
      ...nodes.map((node, index) => {
        if (node.id === id) {
          // console.log()

          return {
            ...node,
            children: newChildrens,
          }
        } else {
          return node
        }
      }),
    ])
    setUpdateNodes(false)
    setIdRoot('')
  }

  const deleteChildrenNode = (id, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [...node.children.filter((item) => item.id !== childId)],
          }
        } else {
          return node
        }
      }),
    ])
    setUpdateChild(true)
  }

  // const deleteRootChildrenNode = (id) => {
  //   console.log(id, nodes)
  //   setNodes([...nodes.filter((node) => node.id !== id)])
  // }

  return (
    <div>
      {nodes.map((node) => (
        <ul>
          <li>
            {node.id}
            <button
              onClick={() => {
                // console.log(nodes)
                setNodes((nodes) => [
                  ...nodes.map((item) => {
                    if (item.id === node.id) {
                      return {
                        ...item,
                        children: [
                          ...item.children,
                          { id: v4(), name: 'child', children: [] },
                        ],
                      }
                    } else {
                      return item
                    }
                  }),
                ])
                setUpdateChild(true)
              }}
            >
              +
            </button>
            <Child
              node={node.children}
              nodes={nodes}
              id={node.id}
              addNode={addChildrenNode}
              deleteNode={deleteChildrenNode}
              idRootOfTree={node.id}
              setNewStateNodes={setNewStateNodes}
              updateNodes={updateNodes}
              setUpdateNodes={() => setIdRoot(node.id)}
              updateChild={setNewStateNodes}
              level={true}
              updateChild1={updateChild}
              setUpdateChild={() => setUpdateChild(false)}
            />
          </li>
        </ul>
      ))}
      {/* {nodes.map((node, index) => (
        <Root
          key={node.id}
          node={node}
          addNode={addRootChildrenNode}
          deleteNode={deleteRootChildrenNode}
        />
      ))} */}
      <button onClick={() => console.log(nodes)}>nodes</button>
      <button
        onClick={() =>
          setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
        }
      >
        add node
      </button>
    </div>
  )
}

export default Tree
