import logo from './logo.svg'
import React, { useState, useEffect } from 'react'
import './App.css'

function App() {
  const [state, setState] = useState({ name: 'hello', children: [] })

  const addNode = () => {
    setState({
      ...state,
      children: [...state.children, { name: 'hello', children: [] }],
    })
  }
  const Node = ({ node, addNode, ind }) => {
    console.log(node)
    const [children, setChildren] = useState(node.children)

    const addChild = (ind) => {
      setChildren([
        ...children,
        children.map((node, index) => {
          if (index === ind) {
            return {
              ...node,
              children: [...node.children, { name: 'hello', children: [] }],
            }
          } else {
            return node
          }
        }),
      ])
    }

    return (
      <ul>
        <li>{node.name}</li>
        <button
          onClick={() => {
            addNode(ind)
          }}
        >
          +
        </button>
        {children.map((item, index) => (
          <Node node={item} addNode={addChild} index={index} />
        ))}
      </ul>
    )
  }

  return (
    <div>
      <Node node={state} addNode={addNode} />
      <button onClick={() => console.log(state)}>nodes</button>
    </div>
  )
}

export default App
