import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'

const Root = ({ node, addNode, deleteNode }) => {
  const [root, setRoot] = useState(node)

  const addChildrenNode = () => {
    setRoot({
      ...root,
      children: [...root.children, { id: v4(), name: 'child', children: [] }],
    })
  }

  const deleteChildrenNode = () => {
    // console.log('artem')
    console.log(root)
    setRoot({
      ...root,
      children: [
        ...root.children.filter((node) => {
          return node
        }),
      ],
    })
  }

  useEffect(() => {
    setRoot(node)
  }, [node])

  return (
    <ul>
      <li>
        {root.id}
        <button onClick={() => addNode(root.id)}>+</button>
        <button onClick={() => deleteNode(root.id)}>-</button>
      </li>
      {root.children.map((node, index) => (
        <Child
          key={node.id}
          node={node}
          addNode={addChildrenNode}
          deleteNode={deleteChildrenNode}
        />
      ))}
    </ul>
  )
}

export default Root
