import React, { useState } from 'react'
import { v4 } from 'uuid'
import Root from './Root'

const Tree = () => {
  const [nodes, setNodes] = useState([
    {
      id: v4(),
      name: 'root',
      children: [
        {
          id: v4(),
          name: 'root',
          children: [{ id: v4(), name: 'root', children: [] }],
        },
      ],
    },
  ])

  const addRootChildrenNode = (id) => {
    console.log(id)
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children,
              { id: v4(), name: 'root', children: [] },
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const deleteRootChildrenNode = (id) => {
    setNodes([...nodes.filter((node) => node.id !== id)])
  }

  return (
    <div>
      {nodes.map((node, index) => (
        <Root
          key={node.id}
          node={node}
          addNode={addRootChildrenNode}
          deleteNode={deleteRootChildrenNode}
        />
      ))}
      <button onClick={() => console.log(nodes)}>nodes</button>
      <button
        onClick={() =>
          setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
        }
      >
        add node
      </button>
    </div>
  )
}

export default Tree
