import logo from './logo.svg'
import React, { useState } from 'react'
import './App.css'

function App() {
  const Node = ({ node }) => {
    const [children, setChildren] = useState(node.children)

    return (
      <ul>
        <li>{node.name}</li>
        {children.map((item) => (
          <Node item={item} />
        ))}
      </ul>
    )
  }

  return (
    <div className="App">
      <Node item={{ name: 'hello', children: [] }} />
    </div>
  )
}

export default App
