import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
// import styles from './Tree.module.css'
import Branch from '../Branch/Branch'
import Icon from '../Icon/Icon'
import Modal from '../Modal/Modal'
import '../../styles/List.css'

const Tree = () => {
  const [nodes, setNodes] = useState([
    {
      id: v4(),
      name: 'mcc-tomsk.de',
      children: [],
    },
  ])

  const [statusUpdateRoot, setStatusUpdateRoot] = useState(false)
  const [statusUpdateChildren, setStatusUpdateChildren] = useState(false)
  const [showModal, setShowModal] = useState()
  const [variant, setVariant] = useState('')

  const addRootToNodes = (value) => {
    setNodes([...nodes, { id: v4(), name: value, children: [] }])
  }

  const deleteRootToNodes = (id) => {
    setNodes([...nodes.filter((node) => node.id !== id)])
  }

  const renameRootToNodes = (id, value) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return { ...node, name: value }
        } else {
          return node
        }
      }),
    ])
  }

  const addChildrenToRoot = (rootId, value) => {
    setNodes((nodes) => [
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children,
              { id: v4(), name: value, children: [] },
            ],
          }
        } else {
          return node
        }
      }),
    ])

    setStatusUpdateChildren(true)
  }

  const addNodeToChildren = (rootId, childId, value) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: value, children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const deleteNodeFromChildren = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.filter((child) => child.id !== childId),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const renameNodeFromChildren = (rootId, childId, value = 'Hello') => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    name: value,
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const updateNodeState = (rootId, childrenStateUpdated) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: childrenStateUpdated,
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateRoot(false)
  }

  useEffect(() => {
    setStatusUpdateRoot(true)
  }, [nodes])

  const Root = ({ id, name }) => {
    const [showModal, setShowModal] = useState(false)
    const [variant, setVariant] = useState('')

    return (
      <li>
        <div style={{ display: 'inline-block' }}>
          <span>{name}</span>
          {/* <div>
            <Icon
              variant={'create'}
              execute={() => {
                setVariant('root-create')
                setShowModal(true)
              }}
            />
          </div> */}
          {/* <button
            onClick={() => {
              setVariant('rename')
              setShowModal(true)
            }}
          >
            r
          </button> */}
        </div>
        {showModal && (
          <Modal
            variant={variant}
            rootId={id}
            rename={renameRootToNodes}
            name={name}
            create={addChildrenToRoot}
            close={() => setShowModal(false)}
          />
        )}
      </li>
    )
  }

  return (
    <div>
      {nodes &&
        nodes.map((node) => (
          <ul key={node.id}>
            <Root id={node.id} name={node.name} />

            <Branch
              nodeChildren={node.children}
              id={node.id}
              updateNodeState={updateNodeState}
              updateChildrenState={updateNodeState}
              statusUpdateRoot={statusUpdateRoot}
              statusUpdateChildren={statusUpdateChildren}
              setStatusUpdateChildren={() => setStatusUpdateChildren(false)}
              addNodeHandler={addNodeToChildren}
              deleteNodeHandler={deleteNodeFromChildren}
              renameNodeHandler={renameNodeFromChildren}
            />
          </ul>
        ))}
      {showModal && (
        <Modal
          variant={variant}
          close={() => setShowModal(false)}
          create={addRootToNodes}
          del={deleteRootToNodes}
          list={nodes}
        />
      )}
      <button onClick={() => console.log(nodes)}>nodes</button>
      <button
        onClick={() => {
          setNodes([{ id: v4(), name: 'mcc-tomsk.de', children: [] }])
        }}
      >
        reset
      </button>
      <button
        onClick={() => {
          setVariant('root-delete')
          setShowModal(true)
        }}
      >
        delete node
      </button>
      <button
        onClick={() => {
          setVariant('root-create')
          setShowModal(true)
        }}
      >
        add node
      </button>
    </div>
  )
}

export default Tree
