import React from 'react'
import styles from './Modal.module.css'

const Modal = ({ show, setShow, variant, roots }) => {
  const [value, setValue] = useState('')

  const RootCreate = () => {
    return (
      <div className={styles.modal}>
        <div className={styles.container}>
          <form>
            <div className={styles.row}>
              <label>Name of node</label>
              <input
                className={styles.input}
                type="text"
                value={value}
                onChange={(e) => setValue(e.target.value)}
              />
            </div>
          </form>
        </div>
      </div>
    )
  }

  const RootDelete = () => {
    return <div className={styles.modal}>RootDelete</div>
  }

  const ChildrenCreate = () => {
    return <div className={styles.modal}>ChildrenCreate</div>
  }

  const modalVariants = {
    'root-create': <RootCreate />,
    'root-delete': <RootDelete />,
    'children-create': <ChildrenCreate />,
  }

  return <div className={styles.modalRoot}>{modalVariants[variant]}</div>
}

export default Modal
