import logo from './logo.svg'
import React, { useState, useEffect } from 'react'
import './App.css'

function App() {
  const [state, setState] = useState({
    name: 'hello',
    children: [{ name: 'hello', children: [{ name: 'hello', children: [] }] }],
  })

  const addNode = () => {
    setState({
      ...state,
      children: [...state.children, { name: 'hello', children: [] }],
    })
  }
  const Node = ({ node, addNode }) => {
    console.log(node)
    const [children, setChildren] = useState(node.children)

    const addChild = () => {
      setChildren({
        ...children,
        children: [...children.children, { name: 'hell01', children: [] }],
      })
    }

    return (
      <ul>
        <li>{node.name}</li>
        <button
          onClick={() => {
            addNode()
          }}
        >
          +
        </button>
        {children.map((item) => (
          <Node node={item} addNode={addChild} />
        ))}
      </ul>
    )
  }

  return (
    <div>
      <Node node={state} addNode={addNode} />
      <button onClick={() => console.log(state)}>nodes</button>
    </div>
  )
}

export default App
