import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Modal from '../Modal/Modal'
// import styles from './Branch.module.css'

const Branch = ({
  nodeChildren,
  id,
  statusUpdateRoot,
  statusUpdateChildren,
  setStatusUpdateChildren,
  updateNodeState,
  updateChildrenState,
  addNodeHandler,
  deleteNodeHandler,
  renameNodeHandler,
}) => {
  const [children, setChildren] = useState(nodeChildren)

  const rootId = id

  const addNodeToChildren = (rootId, childId, value) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: value, children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const deleteNodeFromChildren = (rootId, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.filter((child) => child.id !== childId),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const renameNodeFromChildren = (rootId, childId, value = 'Hello') => {
    setChildren([
      ...children.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    name: value,
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const updateChildren = (id, child) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return { ...node, children: child }
        } else {
          return node
        }
      }),
    ])
  }

  useEffect(() => {
    updateChildrenState(rootId, children)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [children])

  useEffect(() => {
    if (statusUpdateRoot) {
      setChildren(nodeChildren)
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [statusUpdateRoot])

  useEffect(() => {
    if (statusUpdateChildren) {
      setChildren(nodeChildren)
      setStatusUpdateChildren()
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [statusUpdateChildren])

  const Child = ({ id, name }) => {
    const [showModal, setShowModal] = useState(false)
    const [variant, setVariant] = useState('')

    return (
      <li>
        {name}
        <button
          onClick={() => {
            setVariant('root-create')
            setShowModal(true)
          }}
        >
          +
        </button>
        <button
          onClick={() => {
            setVariant('confirmation')
            setShowModal(true)
          }}
        >
          -
        </button>
        <button
          onClick={() => {
            setVariant('rename')
            setShowModal(true)
            // renameNodeHandler(rootId, id)
          }}
        >
          r
        </button>
        {showModal && (
          <Modal
            variant={variant}
            close={() => setShowModal(false)}
            create={addNodeHandler}
            rootId={rootId}
            childId={id}
            execute={() => deleteNodeHandler(rootId, id)}
          />
        )}
      </li>
    )
  }

  return (
    <ul>
      {children &&
        children.map((node) => (
          <div key={node.id}>
            <Child id={node.id} name={node.name} />
            <Branch
              nodeChildren={node.children}
              id={node.id}
              statusUpdateRoot={statusUpdateRoot}
              statusUpdateChildren={statusUpdateChildren}
              setStatusUpdateChildren={setStatusUpdateChildren}
              updateNodeState={updateNodeState}
              updateChildrenState={updateChildren}
              addNodeHandler={addNodeToChildren}
              deleteNodeHandler={deleteNodeFromChildren}
              renameNodeHandler={renameNodeFromChildren}
            />
          </div>
        ))}
    </ul>
  )
}

export default Branch
