import React, { useEffect, useState } from 'react'
import Child from './Child'

function Parent({ node }) {
  const [state, setState] = useState()

  useEffect(() => {
    node ? setState(node) : setState({ id: 1, name: 'empty', children: [] })
  }, [])

  return (
    <ul>
      {state && <li>{state.name}</li>}
      <button
        onClick={() =>
          setState({
            ...state,
            children: [
              ...state.children,
              { id: 1, name: 'empty', children: [] },
            ],
          })
        }
      >
        check
      </button>
      {state && state.children.map((node) => <Parent node={node} />)}
      {/* {state.map((item, index) => {
        return (
          <li>
            <Child item={item} arr={state} setItem={setState} />
          </li>
        );
      })} */}
    </ul>
  )
}

export default Parent
