import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'

const Root = ({ node, addNode, deleteNode }) => {
  const [root, setRoot] = useState(node)

  const addNodeChildren = () => {
    setRoot({
      ...root,
      children: [...root.children, { id: v4(), name: 'root', children: [] }],
    })
  }

  useEffect(() => {
    setRoot(node)
  }, [node])

  return (
    <ul>
      <li>
        {node.id}
        <button
          onClick={
            () => addNode(root.id)
            // setRoot({
            //   ...root,
            //   children: [
            //     ...root.children,
            //     { id: v4(), name: 'root', children: [] },
            //   ],
            // })
          }
        >
          +
        </button>
        <button onClick={() => deleteNode(root.id)}>-</button>
      </li>
      {root.children.map((node, index) => (
        <Child key={node.id} node={node} />
      ))}
    </ul>
  )
}

export default Root
