import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import styles from './Branch.module.css'

const Branch = ({
  nodeChildren,
  id,
  statusUpdateRoot,
  statusUpdateChildren,
  setStatusUpdateChildren,
  updateNodeState,
  updateChildrenState,
  addNodeHandler,
  deleteNodeHandler,
  renameNodeHandler,
}) => {
  const [children, setChildren] = useState(nodeChildren)
  const rootId = id

  const addNodeToChildren = (id, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const deleteNodeFromChildren = (id, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [...node.children.filter((item) => item.id !== childId)],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const updateChildren = (id, child) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return { ...node, children: child }
        } else {
          return node
        }
      }),
    ])
  }

  useEffect(() => {
    updateChildrenState(rootId, children)
  }, [children])

  useEffect(() => {
    if (statusUpdateRoot) {
      setChildren(nodeChildren)
    }
  }, [statusUpdateRoot])

  useEffect(() => {
    if (statusUpdateChildren) {
      setChildren(nodeChildren)
      setStatusUpdateChildren()
    }
  }, [statusUpdateChildren])

  return (
    <ul>
      {children &&
        children.map((node) => (
          <li key={node.id}>
            {node.id}
            <button onClick={() => addNodeHandler(rootId, node.id)}>+</button>
            <button onClick={() => deleteNodeHandler(rootId, node.id)}>
              -
            </button>
            <button onClick={() => renameNodeHandler(rootId)}>r</button>

            <Branch
              nodeChildren={node.children}
              id={node.id}
              statusUpdateRoot={statusUpdateRoot}
              statusUpdateChildren={statusUpdateChildren}
              setStatusUpdateChildren={setStatusUpdateChildren}
              updateNodeState={updateNodeState}
              updateChildrenState={updateChildren}
              addNodeHandler={addNodeToChildren}
              deleteNodeHandler={deleteNodeFromChildren}
            />
          </li>
        ))}
    </ul>
  )
}

export default Branch
