import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'

const Child = ({ node, id, addNode, deleteNode }) => {
  const [children, setChildren] = useState(node)
  const rootId = id

  const addChildrenNode = (id, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  useEffect(() => {
    setChildren(node)
  }, [node])

  return (
    <ul>
      {children.map((node) => (
        <li>
          {node.id}
          <button onClick={() => addNode(rootId, node.id)}>+</button>
          <button onClick={() => deleteNode(rootId, node.id)}>-</button>
          <Child node={node.children} id={node.id} addNode={addChildrenNode} />
        </li>
      ))}
      {/* {}
        <button onClick={() => addNode(children.id)}>+</button>
        <button onClick={() => deleteNode(children.id)}>+</button> */}
      {/* {children.children.map((node, index) => (
          <Child key={node.id} node={node} />
        ))} */}
    </ul>
  )
}

export default Child
