import logo from './logo.svg'
import React, { useState } from 'react'
import './App.css'

function App() {
  const [state, setState] = useState({ name: 'hello', children: [] })
  const Node = ({ node }) => {
    console.log(node)
    const [children, setChildren] = useState(node.children)

    return (
      <ul>
        <li>{node.name}</li>
        {children.map((item) => (
          <Node node={item} />
        ))}
      </ul>
    )
  }

  return (
    <div>
      <Node node={state} />
      <button onClick={() => console.log(state)}>nodes</button>
    </div>
  )
}

export default App
