import React, { useState, useEffect } from 'react'
import { v4 } from 'uuid'

const Branch = ({
  nodes,
  id,
  statusUpdateRoot,
  statusUpdateChildren,
  updateNodeState,
  addNodeHandler,
  deleteNodeHandler,
  setStatusUpdateChildren,
}) => {
  console.log(nodes)
  const [children, setChildren] = useState(nodes)
  const rootId = id

  const addNodeToChildren = (rootId, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const deleteNodeFromChildren = (rootId, childId) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.filter((child) => child.id !== childId),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const updateStateChildren = (rootId, child) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === rootId) {
          return { ...node, children: child }
        } else {
          return node
        }
      }),
    ])
  }

  useEffect(() => {
    updateNodeState(rootId, children)
  }, [children])

  useEffect(() => {
    if (statusUpdateRoot) {
      updateNodeState(nodes)
    }
  }, [statusUpdateRoot])

  useEffect(() => {
    if (statusUpdateChildren) {
      setChildren(nodes)
      setStatusUpdateChildren()
    }
  }, [statusUpdateChildren])

  return (
    <ul>
      {children &&
        children.map((node) => (
          <li>
            {node.id}
            <button onClick={() => addNodeHandler(rootId, node.id)}>+</button>
            <button onClick={() => deleteNodeHandler(rootId, node.id)}>
              -
            </button>

            <Branch
              nodes={node.children}
              id={node.id}
              addNodeHandler={addNodeToChildren}
              deleteNodeHandler={deleteNodeFromChildren}
              statusUpdateRoot={statusUpdateRoot}
              statusUpdateChildren={statusUpdateChildren}
              updateNodeState={updateStateChildren}
              setStatusUpdateChildren={setStatusUpdateChildren}
            />
          </li>
        ))}
    </ul>
  )
}

export default Branch
