import React, { useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'

const Root = ({ node }) => {
  const [root, setRoot] = useState(node)

  return (
    <ul>
      <li>
        {node.id}
        <button
          onClick={() =>
            setRoot({
              ...root,
              children: [
                ...root.children,
                { id: v4(), name: 'root', children: [] },
              ],
            })
          }
        >
          +
        </button>
      </li>
      {root.children.map((node, index) => (
        <Child key={node.id} node={node} />
      ))}
    </ul>
  )
}

export default Root
