const Child = ({ node, id, addNode }) => {
  const [children, setChildren] = useState(node)
  const rootId = id

  const addChildrenNode = () => {
    console.log(children)
  }

  useEffect(() => {
    setChildren(node)
  }, [node])

  return (
    <ul>
      {children.map((node) => (
        <li>
          {node.id}
          <button onClick={() => addNode(rootId, node.id)}>+</button>
          <Child node={node.children} id={node.id} addNode={addChildrenNode} />
        </li>
      ))}
