import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import styles from './Tree.module.css'
import Branch from '../Branch/Branch'

const Tree = () => {
  const [nodes, setNodes] = useState([
    {
      id: v4(),
      name: 'mcc-tomsk.de',
      children: [
        {
          id: v4(),
          name: 'mcc-tomsk.de',
          children: [],
        },
      ],
    },
  ])

  const [statusUpdateRoot, setStatusUpdateRoot] = useState(false)
  const [statusUpdateChildren, setStatusUpdateChildren] = useState(false)

  const addRootToNodes = () => {
    setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
  }

  const addChildrenToRoot = (rootId) => {
    setNodes((nodes) => [
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children,
              { id: v4(), name: 'child', children: [] },
            ],
          }
        } else {
          return node
        }
      }),
    ])

    setStatusUpdateChildren(true)
  }

  const renameChildrenToRoot = (rootId, value = 'Hello') => {
    setNodes((nodes) => [
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            name: value,
          }
        } else {
          return node
        }
      }),
    ])

    setStatusUpdateChildren(true)
  }

  const addNodeToChildren = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const deleteNodeFromChildren = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.filter((child) => child.id !== childId),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const updateNodeState = (rootId, childrenStateUpdated) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: childrenStateUpdated,
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateRoot(false)
  }

  useEffect(() => {
    console.log('tree update')

    setStatusUpdateRoot(true)
  }, [nodes])

  return (
    <div>
      {nodes &&
        nodes.map((node) => (
          <ul key={node.id}>
            <li>
              {node.name}
              <button onClick={() => addChildrenToRoot(node.id)}>+</button>
              <Branch
                nodeChildren={node.children}
                id={node.id}
                updateNodeState={updateNodeState}
                updateChildrenState={updateNodeState}
                statusUpdateRoot={statusUpdateRoot}
                statusUpdateChildren={statusUpdateChildren}
                setStatusUpdateChildren={() => setStatusUpdateChildren(false)}
                addNodeHandler={addNodeToChildren}
                deleteNodeHandler={deleteNodeFromChildren}
                renameNodeHandler={renameChildrenToRoot}
              />
            </li>
          </ul>
        ))}
      <button onClick={() => console.log(nodes)}>nodes</button>
      <button onClick={addRootToNodes}>add node</button>
    </div>
  )
}

export default Tree
