import React, { useState } from 'react'
import styles from './Modal.module.css'

const Modal = ({ close, variant, roots, execution }) => {
  const RootCreate = () => {
    const [value, setValue] = useState('')

    const createHandler = () => {
      if (value.length !== 0) {
        execution(value)
        close()
      }
    }

    return (
      <div className={styles.modal}>
        <div className={styles.container}>
          <form>
            <div className={styles.row}>
              <label>Name of root</label>
              <input
                className={
                  value.length === 0 ? styles.validateInput : styles.input
                }
                type="text"
                placeholder="Enter node name..."
                value={value}
                onChange={(e) => setValue(e.target.value)}
              />
            </div>
            <div className={styles.row}>
              <label className={styles.buttonSubmit} onClick={createHandler}>
                Create
              </label>
              <label className={styles.buttonCancel} onClick={close}>
                Cancel
              </label>
            </div>
          </form>
        </div>
      </div>
    )
  }

  const RootDelete = () => {
    return (
      <div className={styles.modal}>
        <div className={styles.container}>
          <form>
            <div className={styles.row}>
              <label>Name of root</label>
              <select id="country" name="country">
                <option value="australia">Australia</option>
                <option value="canada">Canada</option>
                <option value="usa">USA</option>
              </select>
            </div>
            <div className={styles.row}>
              <label className={styles.buttonSubmit}>Create</label>
              <label className={styles.buttonCancel}>Delete</label>
            </div>
          </form>
        </div>
      </div>
    )
  }

  const ChildrenCreate = () => {
    return <div className={styles.modal}>ChildrenCreate</div>
  }

  const modalVariants = {
    'root-create': <RootCreate />,
    'root-delete': <RootDelete />,
    'children-create': <ChildrenCreate />,
  }

  return <div className={styles.modalRoot}>{modalVariants[variant]}</div>
}

export default Modal
