import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'

function Parent({ node }) {
  const [state, setState] = useState()

  useEffect(() => {
    node ? setState(node) : setState({ id: v4(), name: 'empty', children: [] })
  }, [])

  return (
    <ul>
      {state && <li>{state?.name}</li>}
      <button
        onClick={() =>
          setState({
            ...state,
            children: [
              ...state.children,
              { id: v4(), name: 'empty', children: [] },
            ],
          })
        }
      >
        add
      </button>
      <button onClick={() => setState({})}>del</button>
      {state && state?.children.map((node) => <Parent node={node} />)}
      {/* {state.map((item, index) => {
        return (
          <li>
            <Child item={item} arr={state} setItem={setState} />
          </li>
        );
      })} */}
    </ul>
  )
}

export default Parent
