import React, { useState } from 'react'
import styles from './Modal.module.css'

const Modal = ({ close, variant, list, create, del, rootId, childId }) => {
  const RootCreate = () => {
    const [value, setValue] = useState('')

    const createHandler = () => {
      if (value.length !== 0) {
        if (rootId && !childId) {
          create(rootId, value)
        } else if (rootId && childId) {
          create(rootId, childId, value)
        } else {
          create(value)
        }

        close()
      }
    }

    return (
      <div className={styles.modal}>
        <div className={styles.container}>
          <form>
            <div className={styles.row}>
              <label>Name of root</label>
              <input
                className={
                  value.length === 0 ? styles.validateInput : styles.input
                }
                type="text"
                placeholder="Enter node name..."
                value={value}
                onChange={(e) => setValue(e.target.value)}
              />
            </div>
            <div className={styles.row}>
              <label className={styles.buttonSubmit} onClick={createHandler}>
                Create
              </label>
              <label className={styles.buttonCancel} onClick={close}>
                Cancel
              </label>
            </div>
          </form>
        </div>
      </div>
    )
  }

  const RootDelete = () => {
    const [value, setValue] = useState(list[0].id)

    const deleteHandler = () => {
      if (value !== 0) {
        del(value)
        close()
      }
    }

    return (
      <div className={styles.modal}>
        <div className={styles.container}>
          <form>
            <div className={styles.row}>
              <label>Name of root</label>
              <select onChange={(e) => setValue(e.target.value)} value={value}>
                {list.map((item) => (
                  <option value={item.id}>{item.name}</option>
                ))}
              </select>
            </div>
            <div className={styles.row}>
              <label className={styles.buttonDelete} onClick={deleteHandler}>
                Delete
              </label>
              <label className={styles.buttonCancel} onClick={close}>
                Cancel
              </label>
            </div>
          </form>
        </div>
      </div>
    )
  }

  const Confirmation = () => {
    return (
      <div className={styles.modal}>
        <div className={styles.container}>
          <form>
            <div className={styles.row}>
              <h3>Delete node?</h3>
            </div>
            <div className={styles.row}>
              <label className={styles.buttonDelete} onClick={() => {}}>
                Yes
              </label>
              <label className={styles.buttonCancel} onClick={close}>
                No
              </label>
            </div>
          </form>
        </div>
      </div>
    )
  }

  const modalVariants = {
    'root-create': <RootCreate />,
    'root-delete': <RootDelete />,
    confirmation: <Confirmation />,
  }

  return <div className={styles.modalRoot}>{modalVariants[variant]}</div>
}

export default Modal
