import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Child from './Child'

const Tree = () => {
  const [nodes, setNodes] = useState([
    {
      id: v4(),
      name: 'mcc-tomsk.de',
      children: [],
    },
  ])

  const [statusUpdateRoot, setStatusUpdateRoot] = useState(false)
  const [statusUpdateChildren, setStatusUpdateChildren] = useState(false)

  const [updateNodes, setUpdateNodes] = useState(false)

  const [updateChild, setUpdateChild] = useState(false)

  const addChildrenNode1 = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const deleteChildrenNode1 = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.filter((child) => child.id !== childId),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const updateNodeState1 = (rootId, childrenStateUpdated) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: childrenStateUpdated,
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateRoot(false)
  }

  const addChildrenToRoot = (rootId) => {
    setNodes((nodes) => [
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children,
              { id: v4(), name: 'child', children: [] },
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const addRootToNodes = () => {
    setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
  }

  useEffect(() => {
    console.log('tree update')

    setStatusUpdateRoot(true)
  }, [nodes])

  //

  useEffect(() => {
    console.log('tree update')

    setUpdateNodes(true)
  }, [nodes])

  const addChildrenNode = (id, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])

    setUpdateChild(true)
  }

  const setNewStateNodes = (id, newChildrens) => {
    setNodes([
      ...nodes.map((node, index) => {
        if (node.id === id) {
          return {
            ...node,
            children: newChildrens,
          }
        } else {
          return node
        }
      }),
    ])
    setUpdateNodes(false)
  }

  const deleteChildrenNode = (id, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [...node.children.filter((item) => item.id !== childId)],
          }
        } else {
          return node
        }
      }),
    ])
    setUpdateChild(true)
  }

  return (
    <div>
      {nodes.map((node) => (
        <ul>
          <li>
            {node.id}
            <button
              onClick={() => {
                setNodes((nodes) => [
                  ...nodes.map((item) => {
                    if (item.id === node.id) {
                      return {
                        ...item,
                        children: [
                          ...item.children,
                          { id: v4(), name: 'child', children: [] },
                        ],
                      }
                    } else {
                      return item
                    }
                  }),
                ])
                setUpdateChild(true)
              }}
            >
              +
            </button>
            <Child
              node={node.children}
              nodes={nodes}
              id={node.id}
              addNode={addChildrenNode}
              deleteNode={deleteChildrenNode}
              idRootOfTree={node.id}
              setNewStateNodes={setNewStateNodes}
              updateNodes={updateNodes}
              updateChild={setNewStateNodes}
              level={true}
              updateChild1={updateChild}
              setUpdateChild={() => setUpdateChild(false)}
            />
          </li>
        </ul>
      ))}
      {/* {nodes.map((node, index) => (
        <Root
          key={node.id}
          node={node}
          addNode={addRootChildrenNode}
          deleteNode={deleteRootChildrenNode}
        />
      ))} */}
      <button onClick={() => console.log(nodes)}>nodes</button>
      <button
        onClick={() =>
          setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
        }
      >
        add node
      </button>
    </div>
  )
}

export default Tree
