import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'

const Child = ({
  node,
  id,
  addNode,
  deleteNode,
  idRootOfTree,
  setNewStateNodes,
  updateNodes,
  setUpdateNodes,
  updateChild,
  nodes,
  level,
}) => {
  const [children, setChildren] = useState(node)
  const rootId = id

  console.log(updateNodes)

  const addChildrenNode = (id, childId) => {
    // console.log(children)
    // console.log(id, childId)
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
  }

  const updateChildren = (id, child) => {
    setChildren([
      ...children.map((node) => {
        if (node.id === id) {
          return { ...node, children: child }
        } else {
          return node
        }
      }),
    ])
  }

  useEffect(() => {
    // console.log('children')
    // if (!updateNodes) {
    updateChild(rootId, children)
    // }
    // console.log(children)
    // if (updateNodes) {
    //   console.log('update')
    // setChildren(node)
    //   setUpdateNodes(false)
    // }
  }, [children])

  useEffect(() => {
    console.log('popal', updateNodes)
    if (updateNodes) {
      console.log('updateNodes')
      setChildren(node)

      if (level) {
        console.log('empty')
        setUpdateNodes(false)
      }
    }
    // setChildren(node)
    // console.log(nodes+Date.now)
  }, [updateNodes])

  // useEffect(() => {
  //   // if (idRootOfTree) {
  //   //   // console.log('useEffect')
  //   //   setNewStateNodes(idRootOfTree, children)
  //   //   // console.log('Потомки изменены')
  //   // }
  // }, [children])

  useEffect(() => {
    // console.log(children)
  }, [])

  return (
    <ul>
      {children &&
        children.map((node) => (
          <li>
            {node.id}
            <button onClick={() => addNode(rootId, node.id)}>+</button>
            <button onClick={() => deleteNode(rootId, node.id)}>-</button>
            <button onClick={() => console.log(children)}>log</button>

            <Child
              node={node.children}
              id={node.id}
              addNode={addChildrenNode}
              updateNodes={updateNodes}
              setUpdateNodes={setUpdateNodes}
              idRootOfTree={idRootOfTree}
              setNewStateNodes={setNewStateNodes}
              updateChild={updateChildren}
              nodes={nodes}
              // deleteNode={delete}
            />
          </li>
        ))}
      {/* {}
        <button onClick={() => addNode(children.id)}>+</button>
        <button onClick={() => deleteNode(children.id)}>+</button> */}
      {/* {children.children.map((node, index) => (
          <Child key={node.id} node={node} />
        ))} */}
    </ul>
  )
}

export default Child
