import React, { useEffect, useState } from 'react'
import { v4 } from 'uuid'
import Branch from './Branch'
import Child from './Child'

const Tree = () => {
  const [nodes, setNodes] = useState([
    {
      id: v4(),
      name: 'mcc-tomsk.de',
      children: [
        {
          id: v4(),
          name: 'mcc-tomsk.de',
          children: [],
        },
      ],
    },
  ])

  const [statusUpdateRoot, setStatusUpdateRoot] = useState(false)
  const [statusUpdateChildren, setStatusUpdateChildren] = useState(false)

  const addNodeToChildren = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const deleteNodeFromChildren = (rootId, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.filter((child) => child.id !== childId),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  const updateNodeState = (rootId, childrenStateUpdated) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: childrenStateUpdated,
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateRoot(false)
  }

  const addChildrenToRoot = (rootId) => {
    console.log(nodes)
    console.log(rootId)
    setNodes((nodes) => [
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children,
              { id: v4(), name: 'child', children: [] },
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setUpdateChild(true)

    setStatusUpdateChildren(true)
  }

  const addRootToNodes = () => {
    setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
  }

  useEffect(() => {
    console.log('tree update')

    setStatusUpdateRoot(true)
  }, [nodes])

  //

  const [updateNodes, setUpdateNodes] = useState(false)

  const [updateChild, setUpdateChild] = useState(false)

  useEffect(() => {
    console.log('tree update')

    setStatusUpdateRoot(true)
  }, [nodes])

  const addChildrenNode = (id, childId) => {
    console.log('child')
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [
              ...node.children.map((item) => {
                if (item.id === childId) {
                  return {
                    ...item,
                    children: [
                      ...item.children,
                      { id: v4(), name: 'child', children: [] },
                    ],
                  }
                } else {
                  return item
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])

    setStatusUpdateChildren(true)
  }

  const setNewStateNodes = (id, newChildrens) => {
    setNodes([
      ...nodes.map((node, index) => {
        if (node.id === id) {
          return {
            ...node,
            children: newChildrens,
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateRoot(false)
  }

  const deleteChildrenNode = (id, childId) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return {
            ...node,
            children: [...node.children.filter((item) => item.id !== childId)],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  return (
    <div>
      {/* {nodes.map((node) => (
        <ul>
          <li>
            {node.id}
            <button onClick={() => addChildrenToRoot(node.id)}>+</button>
            <Branch
              nodes={node.children}
              id={node.id}
              statusUpdateRoot={statusUpdateRoot}
              statusUpdateChildren={statusUpdateChildren}
              updateNodeState={updateNodeState}
              addNodeHandler={addNodeToChildren}
              deleteNodeHandler={deleteNodeFromChildren}
              setStatusUpdateChildren={() => setStatusUpdateChildren(false)}
            />
          </li>
        </ul>
      ))} */}
      {nodes.map((node) => (
        <ul>
          <li>
            {node.id}
            <button onClick={() => addChildrenToRoot(node.id)}>+</button>
            <Child
              node={node.children}
              id={node.id}
              addNodeHandler={addChildrenNode}
              deleteNodeHandler={deleteChildrenNode}
              updateNodeState={setNewStateNodes}
              statusUpdateRoot={statusUpdateRoot}
              updateChild={setNewStateNodes}
              statusUpdateChildren={statusUpdateChildren}
              setStatusUpdateChildren={() => setStatusUpdateChildren(false)}
            />
          </li>
        </ul>
      ))}
      {/* {nodes.map((node, index) => (
        <Root
          key={node.id}
          node={node}
          addNode={addRootChildrenNode}
          deleteNode={deleteRootChildrenNode}
        />
      ))} */}
      <button onClick={() => console.log(nodes)}>nodes</button>
      <button
        onClick={() =>
          setNodes([...nodes, { id: v4(), name: 'root', children: [] }])
        }
      >
        add node
      </button>
    </div>
  )
}

export default Tree
