import React, { useState } from 'react'
import { Item } from './Item'
import { IState } from './types'

function App() {
  const [state, setState] = useState<IState[]>([
    {
      name: 'Artem',
      _id: '1',
      roots: [],
      branches: [],
    },
    {
      name: 'Ivan',
      _id: '2',
      roots: [],
      branches: [
        {
          name: 'Popa',
          _id: '1',
          roots: [],
          branches: [
            { name: 'Artem34', _id: '1', roots: [], branches: [] },
            { name: 'Ivan34', _id: '2', roots: [], branches: [] },
          ],
        },
      ],
    },
  ])

  const renameHandler = () => {
    let findNote = state
    let findElem = {}
    const arrayId = ['2', '1', '2']

    arrayId.forEach((id, index, arr) => {
      let len: number = arr.length

      if (index !== len - 1) {
        findNote.filter((item) => {
          if (item._id === id) {
            findNote = item?.branches!
          }
        })
      } else {
        findNote.filter((item) => {
          if (item._id === id) {
            findElem = item
            console.log(findElem)
          }
        })
      }
    })
  }

  renameHandler()

  const renderState = (state: Array<IState>) => {
    // console.log(state)

    return (
      <div>
        {state.map((item, index) => {
          // console.log(item)
          // console.log(item.branches)
          const branch = item.branches!

          return (
            <ul key={index}>
              <Item name={item.name} _id={item._id} setState={setState} />
              {renderState(branch)}
            </ul>
          )
        })}
      </div>
    )
  }

  return <div>{renderState(state)}</div>
}

export default App
