import React, { useState } from 'react'
import { IState } from './types'

function App() {
  const [state, setState] = useState<IState[]>([
    {
      name: 'Artem',
      _id: '1',
      roots: [],
      branches: [
        {
          name: 'Artem2',
          _id: '2',
          roots: [],
          branches: [
            { name: 'Artem3', _id: '3', roots: [], branches: [] },
            { name: 'Ivan3', _id: '4', roots: [], branches: [] },
          ],
        },
      ],
    },
  ])

  const renderState = (state: { name: string }) => {
    // console.log(state)

    return (
      <div>
        {state.map((item, index) => {
          console.log(item)
          // console.log(item.branches)
          const branch = item.branches

          return (
            <div key={index}>
              {item.name}
              <div>{renderState(branch)}</div>
            </div>
          )
        })}
      </div>
    )
  }

  return <div>{renderState(state)}</div>
}

export default App
