import React, { FC, useState } from 'react'
import { v4 } from 'uuid'
import { INode } from '../../types'

const Tree: FC = () => {
  const [nodes, setNodes] = useState<Array<INode>>([
    {
      id: v4(),
      name: 'mcc-tomsk.de',
      children: [],
    },
  ])
  const [statusUpdateRoot, setStatusUpdateRoot] = useState<boolean>(false)
  const [statusUpdateChildren, setStatusUpdateChildren] =
    useState<boolean>(false)
  const [showModal, setShowModal] = useState<boolean>(false)
  const [variant, setVariant] = useState<string>('')

  const addRootToNodes = (value: string) => {
    setNodes([...nodes, { id: v4(), name: value, children: [] }])
  }

  const deleteRootToNodes = (id: string) => {
    setNodes([...nodes.filter((node) => node.id !== id)])
  }

  const renameRootToNodes = (id: string, value: string) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === id) {
          return { ...node, name: value }
        } else {
          return node
        }
      }),
    ])
  }

  const addChildrenToRoot = (rootId: string, value: string) => {
    setNodes((nodes) => [
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children,
              { id: v4(), name: value, children: [] },
            ],
          }
        } else {
          return node
        }
      }),
    ])

    setStatusUpdateChildren(true)
  }

  const addNodeToChildren = (
    rootId: string,
    childId: string,
    value: string
  ) => {
    setNodes([
      ...nodes.map((node) => {
        if (node.id === rootId) {
          return {
            ...node,
            children: [
              ...node.children.map((child) => {
                if (child.id === childId) {
                  return {
                    ...child,
                    children: [
                      ...child.children,
                      { id: v4(), name: value, children: [] },
                    ],
                  }
                } else {
                  return child
                }
              }),
            ],
          }
        } else {
          return node
        }
      }),
    ])
    setStatusUpdateChildren(true)
  }

  return <div></div>
}

export default Tree
