import React, { FC, useEffect, useState } from 'react'
import { INote } from '../types'
import List from './List'
import { v4 } from 'uuid'

interface NoteProps {
  note: INote
}

const Note: FC<NoteProps> = ({ note }) => {
  const [children, setChildren] = useState<Array<INote>>([])
  const [goals, setGoals] = React.useState<
    Array<{
      key: string
      value: string
    }>
  >([])
  const [currentId, setCurrentId] = useState<string>('')
  const [prev, setPrev] = useState<string>(note._id)

  useEffect(() => {
    setChildren(note.childrens)
  }, [])

  const setChildrenHandler = (e: React.MouseEvent) => {
    // console.log(children)
    // console.log(e)
    console.log(children)
    setChildren([...children.filter((item) => item._id !== prev)])
    // const newElem = { _id: v4(), title: 'kek', childrens: [] }

    // if (note._id === id) {
    //   setChildren((prev) => [...prev, newElem])
    // }

    // setGoals((curGoals) => [...curGoals, { key: Math.random().toString(), value: input }])
  }

  return (
    <div>
      <li
        onClick={(e) => {
          setChildrenHandler(e)
        }}
        id={note.prevNote}
      >
        {note.title}
      </li>
      <List
        items={children}
        renderItem={(note: INote) => <Note key={note._id} note={note} />}
      />
    </div>
  )
}

export default Note
