import React, { useState } from 'react'
import { IState } from './types'

function App() {
  const [state, setState] = useState<IState>({
    name: 'Artem',
    _id: '1',
    roots: [],
    branches: [
      {
        name: 'Artem2',
        _id: '2',
        roots: [],
        branches: [
          { name: 'Artem3', _id: '3', roots: [], branches: [] },
          { name: 'Ivan3', _id: '4', roots: [], branches: [] },
        ],
      },
    ],
  })

  const renderState = (state: Array<IState>) => {
    // console.log(state)

    return (
      <div>
        {state.map((item, index) => {
          console.log(item)
          // console.log(item.branches)

          return (
            <div key={index}>
              {item.name}
              <div>
                {item.branches.map((branch, index, arr) => renderState(branch))}
              </div>
            </div>
          )
        })}
      </div>
    )
  }

  return <div>{renderState([...state])}</div>
}

export default App
